mapper.select:
SELECT * FROM (
SELECT @RN := @RN + 1 AS rnum, a.*
FROM (
SELECT * FROM journal_table
ORDER BY idx
) a, (SELECT @RN := 0) b
) c
WHERE c.rnum BETWEEN #{begin} AND #{end}

SELECT * FROM (
SELECT @RN := @RN + 1 AS rnum, a.*
FROM (
SELECT * FROM journal_table WHERE plan_idx = (SELECT plan_idx FROM plan_table WHERE area_code = #{area_code})
ORDER BY idx
) a, (SELECT @RN := 0) b
) c
WHERE c.rnum BETWEEN #{begin} AND #{end}

SELECT * FROM ( SELECT @RN := @RN + 1 AS rnum, a.* FROM ( SELECT * FROM journal_table WHERE reg_date >
NOW() - INTERVAL 3 MONTH NOW() - INTERVAL 6 MONTH NOW() - INTERVAL 12 MONTH NOW() - INTERVAL 3 MONTH
ORDER BY idx ) a, (SELECT @RN := 0) b ) c WHERE c.rnum BETWEEN #{begin} AND #{end}
