mapper.select:
SELECT COUNT(*)
FROM journal_like_table
WHERE journal_idx = #{journalidx}

mapper.update:
UPDATE journal_like_table
SET status = CASE WHEN status = 1 THEN 0 ELSE 1 END
WHERE journal_idx = #{journalidx}

mapper.select:
SELECT status
FROM journal_like_table
WHERE journal_idx = #{journalidx}
