mapper.select:
SELECT * FROM (
SELECT @RN := @RN + 1 AS rnum, a.*
FROM (
SELECT * FROM user_table
ORDER BY idx
) a, (SELECT @RN := 0) b
) c
WHERE c.rnum BETWEEN #{begin} AND #{end}

SELECT idx, email, pw, nick, status
FROM user_table
WHERE email = #{email}
AND pw = #{pw}

mapper.update:
UPDATE user_table
SET nick = #{nick},
pw = #{pw}
WHERE idx = #{idx}

UPDATE user_table
SET status = 1
WHERE idx = #{idx}

mapper.select:
SELECT COUNT(*)
FROM user_table
WHERE idx = #{idx} AND password = #{password}
